import { cn } from '@/lib/utils';
import { Slot } from '@radix-ui/react-slot';
import './prose.css';

// TODO: Maybe cva stuff? Probably not needed.

function Prose({
    className,
    asChild = false,
    ...props
}: React.ComponentProps<'article'> & {
    asChild?: boolean;
}) {
    const Comp = asChild ? Slot : 'article';

    return (
        <Comp
            data-slot='article'
            className={cn('prose', className)}
            {...props}
        />
    );
}

export { Prose };

/* Map typography to shadcn's theme tokens once. */
@layer utilities {
    .prose {
        /* text */
        --tw-prose-body: hsl(var(--foreground));
        --tw-prose-headings: hsl(var(--foreground));
        --tw-prose-lead: hsl(var(--muted-foreground));
        --tw-prose-links: hsl(var(--primary));
        --tw-prose-bold: hsl(var(--foreground));
        --tw-prose-counters: hsl(var(--muted-foreground));
        --tw-prose-bullets: hsl(var(--muted-foreground));
        --tw-prose-quotes: hsl(var(--foreground));
        --tw-prose-quote-borders: hsl(var(--border));
        --tw-prose-captions: hsl(var(--muted-foreground));

        /* code */
        --tw-prose-code: hsl(var(--foreground));
        --tw-prose-code-bg: hsl(var(--muted));
        --tw-prose-pre-code: hsl(var(--muted-foreground));
        --tw-prose-pre-bg: hsl(var(--muted));
        --tw-prose-th-borders: hsl(var(--border));
        --tw-prose-td-borders: hsl(var(--border));
    }

    /* Nice defaults that don't fight your theme */
    .prose :where(code):not(:where(pre *)) {
        background: var(--tw-prose-code-bg);
        padding: 0.125rem 0.375rem;
        border-radius: 0.375rem;
    }
    .prose :where(pre) {
        border: 1px solid hsl(var(--border));
        border-radius: 0.5rem;
        box-shadow: 0 1px 0 rgba(0, 0, 0, 0.03);
    }
    .prose :where(img) {
        border-radius: 0.5rem;
    }
}
